import { Component,Input } from '@angular/core';

@Component({
  selector: 'app-language',
  templateUrl: './language.component.html',
  styleUrls: ['./language.component.css']
})
export class LanguageComponent {
  @Input() languageLogo: any;
  @Input() languageHead: any;
  @Input() languageDescription: any;
}
<div class="languages">
    <img src="{{languageLogo}}" alt="logo" style="float: left;padding: 1%;">
    <h3>{{languageHead}}</h3>
    <p>&bull;{{languageDescription}}</p>
    <hr>
    <img src="../../assets/images/edit-2.png" style="float: right;">
</div>
