import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-child',
  templateUrl: './child.component.html',
  styleUrls: ['./child.component.css']
})
export class ChildComponent {
   @Input() languageLogo: any;
   @Input() languageHead: any;
   @Input() languageDescription: any;
   @Input() educationLogo: any;
   @Input() educationHead: any;
   @Input() educationDescription1: any;
   @Input() educationDescription2: any;
   @Input() educationDescription3: any;
   @Input() experienceLogo: any;
   @Input() experienceHead: any;
   @Input() experienceDescription1: any;
   @Input() experienceDescription2: any;
}
