import { Component,Input } from '@angular/core';

@Component({
  selector: 'app-education',
  templateUrl: './education.component.html',
  styleUrls: ['./education.component.css']
})
export class EducationComponent {
  @Input() educationLogo: any;
  @Input() educationHead: any;
  @Input() educationDescription1: any;
  @Input() educationDescription2: any;
  @Input() educationDescription3: any;
}
<div class="education">
    <img src="{{educationLogo}}" style="float: left; padding: 1%;">
    <h3>{{educationHead}}</h3>
    <div class="container">
        <div class="column">
            <p>{{educationDescription1}}</p>
            <p>&bull;  {{educationDescription2}}</p>
        </div>
        <div class="column">
          <p> {{educationDescription3}}</p>
        </div>
    </div>
    <hr>
    <img src="../../assets/images/edit-2.png" style="float: right;">
</div>
